import React from 'react'

export default function InputBox({type, id, placeholder, width}) {
  return (
    <div className=" mb-3">
    <span className={` inline-block my-1  items-center h-6 border bg-white ${width}`}>
      <input type={type} className="w-full h-6 text-xs p-2" id={id} placeholder={placeholder} />
    </span>
  </div>
  )
}
