import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import LoadingSpinner from "../../../animation/LoadingSpinner";

import logInImg from "../../../img/Hotel/Login.jpg";

const TitleText = styled.h2`
  font-size: 50px;
  font-weight: 900;
  margin: 0;
  margin-bottom: 10px;
`;

const MainBox = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  height: 435px;
  width: 480px;
  margin: 10px;
  opacity: 0.8;
  border-radius: 8px;
`;

const PageWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100%;
`;

const InputWrapper = styled.div`
  display: flex;
  flex-direction: column;
  width: 300px;

`;

const ExplainText = styled.p`
  font-size: 15px;
  color: gray;
  margin: 2px;
`;

const InputBox = styled.input`
  width: 300px;
  height: 30px;
  margin-bottom: 10px;
`;

const SubmitButton = styled.button`
  width: 308px;
  height: 36px;
  font-size: 15px;
  font-weight: bold;
  border: 1px black solid;
  margin-top: 10px;
  background-color:#21cc40;

  &:hover{
    color: white;
    border-color: #21cc40;
  }
`;

export default function SignIn() {
  const [isLoading, setIsLoading] = useState(true);

  setTimeout(() => {
    setIsLoading(false);
  }, 500);

  return (
    <PageWrapper>
      {isLoading ? (
        <LoadingSpinner />
      ) : (
        <MainBox>
          <TitleText>게시판</TitleText>
          <InputWrapper>
            <ExplainText>아이디/이메일</ExplainText>
            <InputBox placeholder="gaesipan@board.com"></InputBox>
            <ExplainText>비밀번호</ExplainText>
            <InputBox placeholder="*******"></InputBox>
            <SubmitButton>로그인</SubmitButton>
          </InputWrapper>
        </MainBox>
      )}
    </PageWrapper>
  );
}
