import React from "react";
import styled, { keyframes } from "styled-components";
import './index.css';
import mainImg from "../../../img/Hotel/main-banner.jpg";

export default function MainPage() {

  function createImage() {
    const img = document.createElement('img');
    img.src = '';
    img.classList.add('falling');
    document.body.appendChild(img);
  }

  setInterval(() => {
    createImage();
  }, 500);

  return (
    <div>
      <MainFrame>
        <MainBox>
          <ButtonWrapper>
            <PlainButton>로그인</PlainButton>
            <PlainButton>회원가입</PlainButton>
            <PlainButton>게시판</PlainButton>
          </ButtonWrapper>
          <FlexWrapper>
            <TitleText>GAESIPAN</TitleText>
            <MiniText>
              Back-End : Shon Jeong Min <br /> Front-End : Hyun Bae Jeong
            </MiniText>
          </FlexWrapper>

          <SubBox src={mainImg} />
        </MainBox>
      </MainFrame>
    </div>
  );
}

const bgColor = "#dbdde9";
const sideColor = "#adacad";

//animation
const falling = keyframes`
  0% {
    transform: translate(0, 0);
  }
  100% {
    transform: translate(0, 100vh);
  }
`;

const FallingImage = styled.img`
  position: absolute;
  left: calc(100% * var(--x));
  top: calc(-100% * var(--y));
  animation: ${falling} 5s linear;
`;
/*
Frame Part
*/

const MainFrame = styled.div`
  background-color: ${bgColor};
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

/*
Box Part
 */
const MainBox = styled.div`
  background-color: white;
  height: 420px;
  width: 700px;
  border-radius: 10px;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  flex-shrink: 0;
`;

const SubBox = styled.img`
  border-radius: 10px;
  margin: 10px;
  margin-top: 0;
  width: 680px;
  height: 285px;
`;

const FlexWrapper = styled.div`
  display: flex;
  align-items: flex-end;
`;

const TitleText = styled.h1`
  line-height: 0;
  font-size: 70px;
  font-weight: bold;
  margin-left: 10px;
  margin-bottom: 35px;
`;

const MiniText = styled.p`
  font-size: 10px;
  margin-bottom: 5px;
  margin-left: 5px;
`;

const ButtonWrapper = styled.div`
  display: flex;
  justify-content: flex-end;
  height: 100%;
  margin: 10px;
`;

const PlainButton = styled.button`
  text-align: center;
  border: 1px solid;
  border-color: black;
  width: 55px;
  height: 20px;
  font-size: 10px;
  font-weight: 300;
  color: ${sideColor};
  border-radius: 5px;
  border-color: ${sideColor};
  margin-left: 5px;

  &:hover {
    background-color: ${bgColor};
    color: #FFFFFF;
  }
`;
