import React from "react";
import styled from "styled-components";
import { useState } from "react";
import { Link } from "react-router-dom";

const ButtonWrapper = styled.div`
  display: flex;
  justify-content: flex-end;
  align-items: center;
  height: 35px;
  padding: 10px;
  border-bottom: 1px solid ${(props) => props.sideColor};;
  border-color: ${(props) => props.sideColor};
`;

const PlainButton = styled.button`
  text-align: center;
  border: 1px solid;
  border-color: black;
  width: 90px;
  height: 25px;
  font-size: 15px;
  font-weight: 500;
  color: ${(props) => props.sideColor};
  border-radius: 10px;
  border-color: ${(props) => props.sideColor};
  margin-left: 8px;

  &:hover {
    background-color: ${(props) => props.bgColor};
    color: #ffffff;
  }
`;

export default function NavBar({
  sideColor,
  bgColor,
  setIsActive,
  setWindowHeight,
  setWindowWidth,
}) {
  function transWindow(event) {
    if (event.target.id === "main-button") {
      setWindowHeight("550px");
      setWindowWidth("900px");
    } else if (event.target.id === "board-button") {
      setWindowHeight("600px");
    } else if (event.target.id === "sign-up-button") {
      setWindowHeight("620px");
    } else if (event.target.id === "sign-in-button") {
      setWindowHeight("500px");
      setWindowWidth("500px");
    }
  }

  return (
    <ButtonWrapper>
      <Link to="/">
        <PlainButton
          id="main-button"
          onClick={transWindow}
          bgColor={bgColor}
          sideColor={sideColor}
        >
          메인화면
        </PlainButton>
      </Link>
      <Link to="/Board">
        <PlainButton
          id="board-button"
          onClick={transWindow}
          bgColor={bgColor}
          sideColor={sideColor}
        >
          게시판
        </PlainButton>
      </Link>
      <Link to="/SignUp">
        <PlainButton
          id="sign-up-button"
          onClick={transWindow}
          bgColor={bgColor}
          sideColor={sideColor}
        >
          회원가입
        </PlainButton>
      </Link>
      <Link to="/SignIn">
        <PlainButton
          id="sign-in-button"
          onClick={transWindow}
          bgColor={bgColor}
          sideColor={sideColor}
        >
          로그인
        </PlainButton>
      </Link>
    </ButtonWrapper>
  );
}
