import React, { useEffect, useState } from "react";
import styled from "styled-components";

import mainImg from "../../../img/Hotel/main-banner.jpg";

const SubBox = styled.img`
  border-radius: 10px;
  margin: 10px;
  margin-top: 0;
  width: 880px;
  height: 380px;
  opacity: 1;
  transition: opacity 1s ease-in-out;
`;

const FlexWrapper = styled.div`
  display: flex;
  align-items: flex-end;
`;

const TitleText = styled.h1`
  line-height: 0;
  font-size: 90px;
  font-weight: bold;
  margin-left: 10px;
  margin-bottom: 50px;
`;

const MiniText = styled.p`
  font-size: 15px;
  margin-bottom: 10px;
  margin-left: 5px;
`;

const ContentWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
`;

export default function index({}) {

  function useCustomState() {
    const [isLoading, setIsLoading] = useState(true);
    // Custom Hook 로직
    return [isLoading, setIsLoading];
  }
  const [isLoading, setIsLoading] = useCustomState;

  setTimeout(() => {
    setIsLoading(false);
  }, 1000);

  return (
   <div>
      {isLoading ? 
      "shie":
       <ContentWrapper> 
      <FlexWrapper>
      <TitleText>GAESIPAN</TitleText>
      <MiniText>
        Back-End : Shon Jeong Min <br /> Front-End : Hyun Bae Jeong
      </MiniText>
    </FlexWrapper>
    <SubBox src={mainImg} />
    </ContentWrapper>}
  </div>
  );
}
