import React from "react";
import styled, { keyframes } from "styled-components";

import mainImg from "../../../img/Hotel/main-banner.jpg";
import petal from "../../../img/animate/leaf.png";


export default function MainPage() {
  return (
    <div>
      <MainFrame>
        {
          Array.from({ length: randomInt(100)+40}, () => [randomInt(100),randomInt(100)]).map((num)=>(
            createFallingImage(num[0],num[1])
          ))

        
        }
        <MainBox>
          <ButtonWrapper>
            <PlainButton>로그인</PlainButton>
            <PlainButton>회원가입</PlainButton>
            <PlainButton>게시판</PlainButton>
          </ButtonWrapper>
          <FlexWrapper>
            <TitleText>GAESIPAN</TitleText>
            <MiniText>
              Back-End : Shon Jeong Min <br /> Front-End : Hyun Bae Jeong
            </MiniText>
          </FlexWrapper>

          <SubBox src={mainImg} />
        </MainBox>
      </MainFrame>
    </div>
  );
}

const bgColor = "#dbdde9";
const sideColor = "#adacad";



/*
animation
*/
const randomInt = (num) => Math.random() * num;


function createFallingImage(xCoord,yCoord){
  
  const falling = keyframes`
    0% {
      opacity: 1;
      transform: translate(0, 0);
      animation-timing-function: cubic-bezier(0.42, 0, 0.58, 1);
    }
    50% {
      transform: translate(${randomInt(100)}vh, 50vh) rotate(${randomInt(180)}deg);
      animation-timing-function: ease-in;
    }
    100% {
      transform: translate(0, 100vh);
      animation-timing-function: cubic-bezier(0.39, 0.575, 0.565, 1);
    }
  `;

  return <FallingImage duration={randomInt(5)+6} delay={randomInt(10)} animate={falling} xCoord={xCoord} yCoord={yCoord} src={petal} />
}

const FallingImage = styled.img`
  opacity: 0;
  position: absolute;
  z-index: 1;
  height: 20px;
  left: ${props => props.xCoord }%;
  top: ${props => props.yCoord }%;
  animation: ${props => props.animate} ${props => props.duration+"s"} linear infinite;
  animation-delay: ${props => props.delay+"s"};
`;





/*
Frame Part
*/

const MainFrame = styled.div`
  position: relative;
  overflow: hidden;
  background-color: ${bgColor};
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

/*
Box Part
 */
const MainBox = styled.div`
  position: relative;
  z-index: 2;
  background-color: white;
  height: 420px;
  width: 700px;
  border-radius: 10px;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  flex-shrink: 0;
`;

const SubBox = styled.img`
  border-radius: 10px;
  margin: 10px;
  margin-top: 0;
  width: 680px;
  height: 285px;
`;

const FlexWrapper = styled.div`
  display: flex;
  align-items: flex-end;
`;

const TitleText = styled.h1`
  line-height: 0;
  font-size: 70px;
  font-weight: bold;
  margin-left: 10px;
  margin-bottom: 35px;
`;

const MiniText = styled.p`
  font-size: 10px;
  margin-bottom: 5px;
  margin-left: 5px;
`;

const ButtonWrapper = styled.div`
  display: flex;
  justify-content: flex-end;
  height: 100%;
  margin: 10px;
`;

const PlainButton = styled.button`
  text-align: center;
  border: 1px solid;
  border-color: black;
  width: 55px;
  height: 20px;
  font-size: 10px;
  font-weight: 300;
  color: ${sideColor};
  border-radius: 5px;
  border-color: ${sideColor};
  margin-left: 5px;

  &:hover {
    background-color: ${bgColor};
    color: #ffffff;
  }
`;
