import React from 'react'
import styled from 'styled-components'

import mainImg from '../../../img/Hotel/main-banner.jpg'

export default function MainPage() {
  return (
    <div>
      <TopArea/>
        <MainFrame>
          <MainBox>
            <ButtonWrapper>
            <PlainButton>로그인</PlainButton>
            <PlainButton>회원가입</PlainButton>
            <PlainButton>게시판</PlainButton>
            </ButtonWrapper>
            
            <TitleText>GAESIPAN</TitleText>
            <SubBox src={mainImg}/>
          </MainBox>
        </MainFrame>
      <BottomArea/>  
    </div>
  )
}



const bgColor = "#dbdde9";
const sideColor = "#adacad";

/*
Frame Part
*/
const TopArea = styled.header`
  background-color: ${bgColor};
  height: 100px;
`;

const BottomArea = styled.footer`
 background-color:  ${bgColor};
 height: 100px;
`;

const MainFrame = styled.div`
  background-color:  ${bgColor};
  display: flex;
  justify-content: center;
`;

/*
Box Part
 */
const MainBox = styled.div`
  background-color: white;
  height: 470px;
  width: 700px;
  border-radius: 10px;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  flex-shrink: 0;
`;

const SubBox = styled.img`
  border-radius: 10px;
  margin: 10px;
  margin-top:0;
  width: 680px;
  height: 285px;
`; 

const TitleText = styled.h1`
  font-size: 70px;
  font-weight: bold;
  margin-left: 10px;
`;

const ButtonWrapper=styled.div`
  display: flex;
  justify-content: flex-end;
  height: 100%;
  margin: 10px;
`;


const PlainButton = styled.button`
  text-align: center;
  border  : 1px solid;
  border-color: black;
  width: 50px;
  height: 20px;
  font-size: 10px;
  font-weight: 300;
  color: ${sideColor};
  border-radius: 5px;
  border-color: ${sideColor};
  margin-left: 5px;
`;
