import { Outlet, Route, Routes } from "react-router-dom";
import MainPage from "./components/pages/MainPage/MainPage.js";
import SignIn from "./components/pages/SignIn/SignIn.js";
import SignUp from "./components/pages/SignUp/SignUp,js";
import Board from "./components/pages/Board";

import React, { useEffect, useState } from "react";
import styled, { keyframes } from "styled-components";

import NavBar from "./components/NavBar";
import FallingPetal from "./animation/FallingPetal";

const bgColor = "#dbdde9";
const sideColor = "#adacad";

/*
Frame Part
*/
const MainFrame = styled.div`
  position: relative;
  overflow: hidden;
  background-color: ${bgColor};
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

/*
Box Part
*/
const MainBox = styled.div`
  position: relative;
  z-index: 2;
  background-color: white;
  height: ${(props) => props.height};
  width: ${(props) => props.width};
  border-radius: 10px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  flex-shrink: 0;
  transition: width 0.5s ease, height 0.5s ease;
  box-shadow: 2px 2px 9px rgba(0, 0, 0, 0.4);
`;


function App() {
  const [windowWidth, setWindowWidth] = useState("900px");
  const [windowHeight, setWindowHeight] = useState("550px");



  /*
  animation
  */
  return (
    <div className="app">
      <MainFrame>
        <FallingPetal />
        <MainBox width={windowWidth} height={windowHeight}>
          <NavBar
            bgColor={bgColor}
            sideColor={sideColor}
            setWindowHeight={setWindowHeight}
            setWindowWidth={setWindowWidth}
          />
          <Routes>
            <Route path="/" element={<MainPage />} />
            <Route path="/SignIn" element={<SignIn />} />
            <Route path="/Signup" element={<SignUp />} />
            <Route path="/Board" element={<Board />} />
          </Routes>
        </MainBox>
      </MainFrame>
    </div>
  );
}

export default App;
